$(document).ready(function(){for(var t=[],e=[],i=0;14>i;i+=.5)t.push([i,Math.sin(i)]),e.push([i,Math.cos(i)]);$.plot($(".chart"),[{data:t,label:"sin(x)",color:"#BA1E20"},{data:e,label:"cos(x)",color:"#459D1C"}],{series:{lines:{show:!0},points:{show:!0}},grid:{hoverable:!0,clickable:!0},yaxis:{min:-1.6,max:1.6}});var n=null;$(".chart").bind("plothover",function(t,e,i){if(i){if(n!=i.dataIndex){n=i.dataIndex,$("#tooltip").fadeOut(200,function(){$(this).remove()});var s=i.datapoint[0].toFixed(2),o=i.datapoint[1].toFixed(2);unicorn.flot_tooltip(i.pageX,i.pageY,i.series.label+" of "+s+" = "+o)}}else $("#tooltip").fadeOut(200,function(){$(this).remove()}),n=null});for(var s=[],o=Math.floor(10*Math.random())+1,i=0;o>i;i++)s[i]={label:"Series"+(i+1),data:Math.floor(100*Math.random())+1};$.plot($(".pie"),s,{series:{pie:{show:!0,radius:.75,label:{show:!0,radius:.75,formatter:function(t,e){return'<div style="font-size:8pt;text-align:center;padding:2px;color:white;">'+t+"<br/>"+Math.round(e.percent)+"%</div>"},background:{opacity:.5,color:"#000"}},innerRadius:.2},legend:{show:!1}}});for(var r=[],i=0;10>=i;i+=1)r.push([i,parseInt(30*Math.random())]);var s=new Array;s.push({data:r,bars:{show:!0,barWidth:.4,order:1}}),$.plot($(".bars"),s,{legend:!0})}),unicorn={flot_tooltip:function(t,e,i){$('<div id="tooltip">'+i+"</div>").css({top:e+5,left:t+5}).appendTo("body").fadeIn(200)}};